import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import InputRange from 'react-input-range';

function App() {

  const [numberOfCharacters, setNumberOfCharacters] = useState(10);
  const [includeUppercase, setIncludeUppercase] = useState(false);
  const [includeNumbers, setIncludeNumbers] = useState(false);
  const [includeSymbols, setIncludeSymbols] = useState(false);

  const onRangeChange = (event) => {
    setNumberOfCharacters(event.target.value);
  }

  const generatePassword = () => {

  }
  return (
    <div className="App">
      <div className = "container">
        <h1 className = "title">Password generator</h1>
        <h3 id = "passwordDisplay" class = "password-display">password</h3>
        <div className = "form">
          <p>Number of characters</p>
          <div>
            <input 
              type = "range" 
              min="1" 
              max="30" 
              value={numberOfCharacters}
              onChange = {onRangeChange}
              id="characterAmountRange"
            />
            <input 
              type = "number"
              min="1" 
              max="50" 
              value={numberOfCharacters}
              onChange = {onRangeChange}
              id="characterAmountNumber"  
              class = "number-input"
            />
          </div>
          <p for="includeUppercase">Include uppercase</p>
          <input type = "checkbox" id = "includeUppercase"/>

          <p for="includeNumbers">Include numbers</p>
          <input type = "checkbox" id = "includeNumbers"/>

          <p for="includeSymbols">Include symbols</p>
          <input type = "checkbox" id = "includeSymbols"/>

          <button 
            class = "btn" 
            onClick = {generatePassword}
          >
            Generate password
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
